import 'package:flutter/material.dart';

class CountryData {
  int id;
  String name;
  String flag;
  String square;
  String info;
  List<String> listPhoto;

  CountryData(this.id, this.name, this.flag, this.square, this.info, this.listPhoto);


  List<Widget> GetListOfPhoto(){

    List<Widget> pages = [];

    for(int i = 0; i < listPhoto.length; i++)
    {
      pages.add(Image.network(listPhoto[i]));
    }


    return pages;
  }
}

